// Nettoie et transforme le JSON "top_left / bottom_right"
// en crop pr√™t pour le node Edit Image ‚Üí Crop

const output = [];

for (const item of $input.all()) {
  let raw = item.json.content;

  // Supprime les balises ```json et ```
  raw = raw.replace(/```json/g, "").replace(/```/g, "").trim();

  // Parse le JSON
  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (e) {
    throw new Error("Impossible de parser le JSON : " + raw);
  }

  // R√©cup√®re les coordonn√©es
  const coords = parsed.photo_coordinates || parsed;
  const topLeft = coords.top_left;
  const bottomRight = coords.bottom_right;

  // Calcule x, y, width, height
  const x = topLeft.x;
  const y = topLeft.y;
  const width = bottomRight.x - topLeft.x;
  const height = bottomRight.y - topLeft.y;

  // Pr√©pare la sortie pour Edit Image ‚Üí Crop
  output.push({
    json: {
      crop: { x, y, width, height },
    },
  });
}

return output;

