const output = [];

for (const item of $input.all()) {
  const emailId = item.json.id;

  // PJ binaires
  if (item.binary && Object.keys(item.binary).length > 0) {
    for (const [key, binaryData] of Object.entries(item.binary)) {
      output.push({
        json: {
          email_id: emailId,
          attachmentName: binaryData.fileName || key,
          contentType: binaryData.mimeType || 'unknown',
        },
        binary: {
          data: binaryData
        }
      });
    }
  }

  // PJ dans json.attachments (si applicable)
  else if (Array.isArray(item.json.attachments)) {
    for (const attachment of item.json.attachments) {
      output.push({
        json: {
          gmail_id: emailId,
          attachmentId: attachment.attachmentId,
          filename: attachment.filename,
          mimeType: attachment.mimeType,
          size: attachment.size
        }
      });
    }
  }
}

return output;

