  const body = item.json.text ?? item.json.html ?? '';

  // Comptage des pi√®ces jointes
  let count = 0;
  if (item.binary && typeof item.binary === 'object' && Object.keys(item.binary).length) {
    count = Object.keys(item.binary).length;
  } else if (Array.isArray(item.json.attachments)) {
    count = item.json.attachments.length;
  }

  // Construction de la sortie
  output.push({
    json: {
      email_id: emailId,
      subject,
      body,
      attachments_count: count
    }
  });
}

return output;

