/**
 * Transforme n‚Äôimporte quel nom en cl√© 100 % s√ªre pour Supabase Storage
 * - enl√®ve les accents
 * - remplace les caract√®res interdits par _
 * - condense les doubles _
 */
function toSafeFileName(name = 'file.bin') {
  return name
    // 1Ô∏è‚É£ S√©pare les signes diacritiques puis les supprime
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')

    // 2Ô∏è‚É£ Remplace tout caract√®re NON pr√©sent dans la whitelist S3/Supa
    .replace(/[^A-Za-z0-9 !_\-.()*'&$@=;:+,?]/g, '_')

    // 3Ô∏è‚É£ Condense __ successifs, retire _ en d√©but/fin
    .replace(/_+/g, '_')
    .replace(/^_+|_+$/g, '');
}

