const bucketName = 'email-attachment';
const projectUrl = 'https://drrgcimyszqllymfxqbm.supabase.co';
const today = new Date().toISOString().slice(0, 10); // ex. 2025-07-07

return $input.all().map(item => {
  const bin = item.binary?.data;
  const emailId = item.json.email_id;

  if (!emailId) {
    throw new Error('email_id manquant dans l‚Äôitem ‚Äì injecte-le avant ce node');
  }
  if (!bin) {
    throw new Error('Pi√®ce jointe absente dans binary.data');
  }

  const rawFileName = bin.fileName || 'file.bin';
  const mimeType    = bin.mimeType || 'application/octet-stream';
  const fileSize    = bin.fileSize ?? Buffer.from(bin.data, 'base64').length;

  // üöÄ Ici la magie :
  const safeFileName = toSafeFileName(rawFileName);
  const filePath     = `${today}/${emailId}/${safeFileName}`;

  return {
    json: {
      email_id:    emailId,
      file_name:   rawFileName,
      file_type:   mimeType,
      file_size:   fileSize,
      file_path:   filePath,
      uploaded_at: new Date().toISOString(),

      // URLs d‚Äôupload direct
      upload_rest_url: `${projectUrl}/storage/v1/object/${bucketName}/${filePath}`,
      upload_s3_url:   `${projectUrl}/storage/v1/s3/${bucketName}/${filePath}`,
    },
    binary: { data: bin },
  };
});

