const MIN_SIZE = 25 * 1024; // 45 Ko en octets

// Fonction utilitaire pour convertir "229 kB", "7.05 kB", etc. en octets
function parseSize(sizeStr) {
  const match = sizeStr.match(/^([\d.]+)\s*(kB|MB|B)$/i);
  if (!match) return 0;

  const value = parseFloat(match[1]);
  const unit = match[2].toLowerCase();

  if (unit === 'kb') return value * 1024;
  if (unit === 'mb') return value * 1024 * 1024;
  if (unit === 'b')  return value;
  return 0;
}

// Filtrer les items en fonction de leur taille,
// mais garder les PDF quelle que soit leur taille
const filtered = $input.all().filter(item => {
  const fileName = item.json.file_name || ''; // ou le champ correspondant dans tes donn√©es
  const sizeStr = item.json.file_size;
  const bytes = parseSize(sizeStr);

  const isPDF = /\.pdf$/i.test(fileName);
  return isPDF || bytes >= MIN_SIZE;
});

return filtered;

