// EntrÃ©e attendue : [{ email, tickets: [{titre, description, url, statut, reponse}, ...] }, ...]
const groups = $input.all();

function cleanDesc(str = '', max = 300) {
  // Retire les [cid:...] et <mailto:...> et compresse les espaces
  let s = str
    .replace(/\[cid:[^\]]+\]/g, '')
    .replace(/<mailto:[^>]+>/g, '')
    .replace(/\s{2,}/g, ' ')
    .trim();
  if (s.length > max) s = s.slice(0, max) + 'â€¦';
  return s;
}

function htmlEscape(s = '') {
  return s
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');
}

const items = [];

for (const g of groups) {
  const to = g.json.email || 'unknown@example.com';
  const tickets = Array.isArray(g.json.tickets) ? g.json.tickets : [];

  // Texte brut
  const lines = tickets.map(t =>
    [
      `URL: ${t.url || ''}`,
      `Statut: ${t.statut || ''}`,
      `Titre: ${t.titre || ''}`,
      `Description: ${cleanDesc(t.description)}`,
      `RÃ©ponse: ${cleanDesc(t.reponse)}`
    ].join('\n')
  );

  // Tableau HTML
  const rows = tickets.map((t, i) => {
    const desc = htmlEscape(cleanDesc(t.description));
    const titre = htmlEscape(t.titre || '');
    const statut = htmlEscape(t.statut || '');
    const url = t.url ? `<a href="${t.url}" target="_blank">${t.url}</a>` : '';
    const reponse = htmlEscape(cleanDesc(t.reponse));
    const bg = i % 2 === 0 ? '#ffffff' : '#f9fafc';
    return `
      <tr style="background:${bg};">
        <td style="padding:10px;vertical-align:top;border:1px solid #dee2e6;">${url}</td>
        <td style="padding:10px;vertical-align:top;border:1px solid #dee2e6;">${statut}</td>
        <td style="padding:10px;vertical-align:top;border:1px solid #dee2e6;">${titre}</td>
        <td style="padding:10px;vertical-align:top;border:1px solid #dee2e6;">${desc}</td>
        <td style="padding:10px;vertical-align:top;border:1px solid #dee2e6;">${reponse}</td>
      </tr>`;
  }).join('');

  const textBody =
`Bonjour,

Les tickets suivants ont changÃ© de statut :

${lines.map(l => `â€”\n${l}`).join('\n\n')}

Bonne journÃ©e,`;

  const htmlBody = `
<div style="font-family:Arial,Helvetica,sans-serif;font-size:14px;line-height:1.5;color:#333;">
  <p>Bonjour,</p>
  <p>Les tickets suivants ont changÃ© de statut :</p>
  <table cellpadding="0" cellspacing="0" style="border-collapse:collapse;min-width:600px;border:1px solid #dee2e6;">
    <thead>
      <tr style="background-color:#f0f4f8;color:#333;text-align:left;">
