        <th style="padding:10px;border:1px solid #dee2e6;">Description</th>
        <th style="padding:10px;border:1px solid #dee2e6;">RÃ©ponse</th>
      </tr>
    </thead>
    <tbody>
      ${rows}
    </tbody>
  </table>
  <p style="margin-top:20px;">Bonne journÃ©e,</p>
</div>`;

  items.push({
    json: {
      to,
      subject: `ðŸ“ ${tickets.length} ticket(s) ont changÃ© de statut`,
      text: textBody,
      html: htmlBody,
    }
  });
}

return items;

// RÃ©cupÃ©rer tous les items d'entrÃ©e
const items = $input.all();

// Dictionnaire pour regrouper par adresse email
const grouped = {};

for (const item of items) {
  const data = item.json;
  const email = data.properties?.fromEmail?.email || 'unknown';

  // Extraire les champs demandÃ©s
  const titre = data.properties?.Titre?.title?.[0]?.plain_text || '';
  const description = data.properties?.Description?.rich_text?.[0]?.plain_text || '';  
  const reponse = data.properties?.reponse?.rich_text?.[0]?.plain_text || '';
  const url = data.url || '';
  const statut = data.properties?.Statut?.status?.name || '';

  if (!grouped[email]) {
    grouped[email] = [];
  }

  grouped[email].push({
    titre,
    description,
    reponse,
    url,
    statut
  });
}

// Transformer le regroupement en tableau d'items n8n
return Object.keys(grouped).map(email => ({
  json: {
    email,
    tickets: grouped[email]
  }
}));
